import React, { useState, useEffect } from 'react';
import FileUpload from './components/FileUpload';
import SearchForm from './components/SearchForm';
import SearchResults from './components/SearchResults';
import { SearchQuery, SearchResult, UploadResponse } from './types';
import { searchDocuments, getDocumentCount, clearDocuments } from './services/api';

const App: React.FC = () => {
  const [searchResults, setSearchResults] = useState<SearchResult[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const [documentCount, setDocumentCount] = useState(0);
  const [lastQuery, setLastQuery] = useState('');
  const [uploadMessage, setUploadMessage] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchDocumentCount();
  }, []);

  const fetchDocumentCount = async () => {
    try {
      const response = await getDocumentCount();
      setDocumentCount(response.count);
    } catch (error) {
      console.error('„Éâ„Ç≠„É•„É°„É≥„ÉàÊï∞„ÅÆÂèñÂæó„Å´Â§±Êïó:', error);
    }
  };

  const handleUploadSuccess = (response: UploadResponse) => {
    setUploadMessage(`‚úÖ ${response.message}`);
    setDocumentCount(response.total_documents);
    setError('');
    setTimeout(() => setUploadMessage(''), 5000);
  };

  const handleUploadError = (errorMessage: string) => {
    setError(`‚ùå ${errorMessage}`);
    setUploadMessage('');
  };

  const handleSearch = async (query: SearchQuery) => {
    if (documentCount === 0) {
      setError('„Åæ„ÅöPDF„Éï„Ç°„Ç§„É´„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åó„Å¶„Åè„Å†„Åï„ÅÑ');
      return;
    }

    setIsSearching(true);
    setError('');
    setLastQuery(query.query);

    try {
      const results = await searchDocuments(query);
      setSearchResults(results);
    } catch (error: any) {
      setError(`Ê§úÁ¥¢„Ç®„É©„Éº: ${error.response?.data?.detail || error.message}`);
      setSearchResults([]);
    } finally {
      setIsSearching(false);
    }
  };

  const handleClearDocuments = async () => {
    if (window.confirm('„Åô„Åπ„Å¶„ÅÆ„Éâ„Ç≠„É•„É°„É≥„Éà„ÇíÂâäÈô§„Åó„Åæ„Åô„ÅãÔºü')) {
      try {
        await clearDocuments();
        setDocumentCount(0);
        setSearchResults([]);
        setUploadMessage('‚úÖ „Åô„Åπ„Å¶„ÅÆ„Éâ„Ç≠„É•„É°„É≥„Éà„ÅåÂâäÈô§„Åï„Çå„Åæ„Åó„Åü');
        setTimeout(() => setUploadMessage(''), 3000);
      } catch (error: any) {
        setError(`ÂâäÈô§„Ç®„É©„Éº: ${error.response?.data?.detail || error.message}`);
      }
    }
  };

  return (
    <div style={{
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '20px',
      fontFamily: 'Arial, sans-serif'
    }}>
      <header style={{ 
        textAlign: 'center', 
        marginBottom: '40px',
        borderBottom: '2px solid #007bff',
        paddingBottom: '20px'
      }}>
        <h1 style={{ 
          color: '#007bff', 
          margin: '0 0 10px 0',
          fontSize: '32px'
        }}>
          üìö PDFË≥áÊñôÊ§úÁ¥¢„ÉÑ„Éº„É´
        </h1>
        <p style={{ 
          color: '#666', 
          margin: '0',
          fontSize: '16px'
        }}>
          PDF„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åó„Å¶„Éè„Ç§„Éñ„É™„ÉÉ„ÉâÊ§úÁ¥¢„ÅßÂøÖË¶Å„Å™ÊÉÖÂ†±„ÇíË¶ã„Å§„Åë„Åæ„Åó„Çá„ÅÜ
        </p>
      </header>

      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between', 
        alignItems: 'center',
        marginBottom: '20px',
        padding: '15px',
        backgroundColor: '#f8f9fa',
        borderRadius: '8px',
        border: '1px solid #e9ecef'
      }}>
        <div>
          <span style={{ fontWeight: 'bold' }}>
            ÁôªÈå≤Ê∏à„Åø„Éâ„Ç≠„É•„É°„É≥„Éà: {documentCount}‰ª∂
          </span>
        </div>
        {documentCount > 0 && (
          <button
            onClick={handleClearDocuments}
            style={{
              backgroundColor: '#dc3545',
              color: 'white',
              border: 'none',
              padding: '8px 16px',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '14px'
            }}
          >
            „Åô„Åπ„Å¶ÂâäÈô§
          </button>
        )}
      </div>

      {(uploadMessage || error) && (
        <div style={{
          padding: '15px',
          marginBottom: '20px',
          borderRadius: '8px',
          backgroundColor: error ? '#f8d7da' : '#d4edda',
          border: error ? '1px solid #f5c6cb' : '1px solid #c3e6cb',
          color: error ? '#721c24' : '#155724'
        }}>
          {uploadMessage || error}
        </div>
      )}

      <div style={{ marginBottom: '40px' }}>
        <h2 style={{ marginBottom: '20px', color: '#333' }}>
          üì§ PDF„Éï„Ç°„Ç§„É´„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ
        </h2>
        <FileUpload
          onUploadSuccess={handleUploadSuccess}
          onUploadError={handleUploadError}
        />
      </div>

      <div style={{ marginBottom: '40px' }}>
        <h2 style={{ marginBottom: '20px', color: '#333' }}>
          üîç „Éâ„Ç≠„É•„É°„É≥„ÉàÊ§úÁ¥¢
        </h2>
        <SearchForm
          onSearch={handleSearch}
          isSearching={isSearching}
        />
      </div>

      {(searchResults.length > 0 || (lastQuery && !isSearching)) && (
        <div>
          <h2 style={{ marginBottom: '20px', color: '#333' }}>
            üìã Ê§úÁ¥¢ÁµêÊûú
          </h2>
          <SearchResults
            results={searchResults}
            query={lastQuery}
          />
        </div>
      )}

      <footer style={{
        marginTop: '60px',
        padding: '20px 0',
        textAlign: 'center',
        borderTop: '1px solid #e9ecef',
        color: '#666',
        fontSize: '14px'
      }}>
        <p>
          ü§ñ „Éè„Ç§„Éñ„É™„ÉÉ„ÉâÊ§úÁ¥¢ („Çª„Éû„É≥„ÉÜ„Ç£„ÉÉ„ÇØ + „Ç≠„Éº„ÉØ„Éº„Éâ) „Å´„Çà„ÇäÈ´òÁ≤æÂ∫¶„Å™Ê§úÁ¥¢„ÇíÂÆüÁèæ
        </p>
        <p style={{ margin: '5px 0 0 0' }}>
          Powered by OpenAI API & ChromaDB
        </p>
      </footer>
    </div>
  );
};

export default App;
