import React, { useState, useEffect } from 'react';
import SearchForm from '../components/SearchForm';
import SearchResults from '../components/SearchResults';
import { SearchQuery, FileSearchResult } from '../types';
import { searchFiles, getDocumentCount } from '../services/api';

interface SearchPageProps {
  onSearchComplete: (results: FileSearchResult[], query: string) => void;
}

const SearchPage: React.FC<SearchPageProps> = ({ onSearchComplete }) => {
  const [searchResults, setSearchResults] = useState<FileSearchResult[]>([]);
  const [isSearching, setIsSearching] = useState(false);
  const [documentCount, setDocumentCount] = useState(0);
  const [lastQuery, setLastQuery] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchDocumentCount();
  }, []);

  const fetchDocumentCount = async () => {
    try {
      const response = await getDocumentCount();
      setDocumentCount(response.count);
    } catch (error) {
      console.error('ドキュメント数の取得に失敗:', error);
    }
  };

  const handleSearch = async (query: SearchQuery) => {
    if (documentCount === 0) {
      setError('まずアップロードページでPDFファイルをアップロードしてください');
      return;
    }

    setIsSearching(true);
    setError('');
    setLastQuery(query.query);

    try {
      const results = await searchFiles(query);
      setSearchResults(results);
      onSearchComplete(results, query.query);
    } catch (error: any) {
      setError(`検索エラー: ${error.response?.data?.detail || error.message}`);
      setSearchResults([]);
    } finally {
      setIsSearching(false);
    }
  };

  return (
    <div style={{ padding: '40px 20px' }}>
      {/* ページタイトル */}
      <div style={{ textAlign: 'center', marginBottom: '40px' }}>
        <h1 style={{ 
          fontSize: '32px', 
          margin: '0 0 15px 0', 
          color: '#333',
          fontWeight: 'bold'
        }}>
          🔍 ドキュメント検索
        </h1>
        <p style={{ 
          fontSize: '16px', 
          color: '#666', 
          margin: '0',
          lineHeight: '1.6'
        }}>
          登録されたPDFファイルからハイブリッド検索（セマンティック + キーワード）で情報を検索
        </p>
      </div>

      {/* 現在のドキュメント状況 */}
      <div style={{
        backgroundColor: documentCount > 0 ? '#e8f5e8' : '#fff3cd',
        border: `2px solid ${documentCount > 0 ? '#28a745' : '#ffc107'}`,
        borderRadius: '12px',
        padding: '25px',
        marginBottom: '30px',
        textAlign: 'center'
      }}>
        <div style={{ 
          fontSize: '48px', 
          marginBottom: '15px',
          color: documentCount > 0 ? '#28a745' : '#856404'
        }}>
          {documentCount > 0 ? '📚' : '📁'}
        </div>
        <h3 style={{ 
          margin: '0 0 10px 0', 
          fontSize: '24px',
          color: documentCount > 0 ? '#155724' : '#856404'
        }}>
          {documentCount > 0 ? `${documentCount}件のドキュメントが利用可能` : 'ドキュメントが登録されていません'}
        </h3>
        <p style={{ 
          margin: '0', 
          fontSize: '16px',
          color: documentCount > 0 ? '#155724' : '#856404'
        }}>
          {documentCount > 0 
            ? 'ハイブリッド検索で情報を検索できます' 
            : 'まずアップロードページでPDFファイルをアップロードしてください'}
        </p>
      </div>

      {error && (
        <div style={{
          padding: '15px',
          marginBottom: '30px',
          borderRadius: '8px',
          backgroundColor: '#f8d7da',
          border: '1px solid #f5c6cb',
          color: '#721c24',
          fontSize: '16px',
          textAlign: 'center',
          fontWeight: 'bold'
        }}>
          {error}
        </div>
      )}

      {/* 検索フォーム */}
      <div style={{
        backgroundColor: 'white',
        border: '2px solid #e9ecef',
        borderRadius: '12px',
        padding: '30px',
        marginBottom: '30px',
        boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
      }}>
        <h2 style={{ 
          marginBottom: '20px', 
          color: '#333',
          fontSize: '24px',
          textAlign: 'center'
        }}>
          🔍 検索クエリを入力
        </h2>
        <SearchForm
          onSearch={handleSearch}
          isSearching={isSearching}
        />
      </div>

      {/* 検索結果 */}
      {(searchResults.length > 0 || (lastQuery && !isSearching)) && (
        <div style={{
          backgroundColor: 'white',
          border: '2px solid #e9ecef',
          borderRadius: '12px',
          padding: '30px',
          boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            marginBottom: '25px'
          }}>
            <h2 style={{ margin: '0', color: '#333', fontSize: '24px' }}>
              📋 検索結果
            </h2>
            {searchResults.length > 0 && (
              <div style={{
                backgroundColor: '#28a745',
                color: 'white',
                padding: '8px 16px',
                borderRadius: '20px',
                fontSize: '14px',
                fontWeight: 'bold'
              }}>
                {searchResults.length}件のファイルが見つかりました
              </div>
            )}
          </div>
          <SearchResults
            results={searchResults}
            query={lastQuery}
          />

          {searchResults.length > 0 && (
            <div style={{
              marginTop: '30px',
              padding: '20px',
              backgroundColor: '#f8f9fa',
              borderRadius: '8px',
              textAlign: 'center'
            }}>
              <p style={{ 
                margin: '0 0 10px 0', 
                fontSize: '16px', 
                fontWeight: 'bold',
                color: '#333'
              }}>
                💡 検索結果を活用してAI資料を生成しませんか？
              </p>
              <p style={{ 
                margin: '0', 
                fontSize: '14px', 
                color: '#666'
              }}>
                「資料生成」ページで検索結果を基に要約・レポート・プレゼン資料を自動作成できます
              </p>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default SearchPage;
