import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import UploadPage from './pages/UploadPage';
import SearchPage from './pages/SearchPage';
import DocumentGeneratorPage from './pages/DocumentGeneratorPage';
import { FileSearchResult } from './types';
import { getDocumentCount } from './services/api';
import { FaRobot, FaHeart } from 'react-icons/fa';

const App: React.FC = () => {
  const [currentPage, setCurrentPage] = useState<'upload' | 'search' | 'generate'>('upload');
  const [searchResults, setSearchResults] = useState<FileSearchResult[]>([]);
  const [lastQuery, setLastQuery] = useState('');
  const [documentCount, setDocumentCount] = useState(0);

  useEffect(() => {
    fetchDocumentCount();
  }, []);

  const fetchDocumentCount = async () => {
    try {
      const response = await getDocumentCount();
      setDocumentCount(response.count);
    } catch (error) {
      console.error('ドキュメント数の取得に失敗:', error);
    }
  };

  const handleSearchComplete = (results: FileSearchResult[], query: string) => {
    setSearchResults(results);
    setLastQuery(query);
  };

  const handleNavigate = (page: 'upload' | 'search' | 'generate') => {
    setCurrentPage(page);
  };

  const handleDocumentCountChange = (count: number) => {
    setDocumentCount(count);
  };

  return (
    <div style={{
      minHeight: '100vh',
      backgroundColor: '#f8f9fa',
      fontFamily: 'Arial, sans-serif'
    }}>
      <Header
        currentPage={currentPage}
        onNavigate={handleNavigate}
        documentCount={documentCount}
      />

      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        backgroundColor: 'white',
        minHeight: 'calc(100vh - 140px)',
        boxShadow: '0 0 10px rgba(0,0,0,0.1)',
        display: 'flex',
        flexDirection: 'column'
      }}>
        <div style={{ flex: 1 }}>
          {currentPage === 'upload' && (
            <UploadPage onDocumentCountChange={handleDocumentCountChange} />
          )}

          {currentPage === 'search' && (
            <SearchPage onSearchComplete={handleSearchComplete} />
          )}

          {currentPage === 'generate' && (
            <DocumentGeneratorPage 
              searchResults={searchResults}
              query={lastQuery}
            />
          )}
        </div>
      </div>

      <footer style={{
        background: 'linear-gradient(135deg, #1a1a2e 0%, #16213e 50%, #0f3460 100%)',
        color: '#b8c6db',
        borderTop: '1px solid rgba(255,255,255,0.1)',
        position: 'relative'
      }}>
        {/* 微妙な背景テクスチャ */}
        <div style={{
          position: 'absolute',
          top: '0',
          left: '0',
          right: '0',
          bottom: '0',
          background: `
            repeating-linear-gradient(
              90deg,
              transparent,
              transparent 2px,
              rgba(255,255,255,0.03) 2px,
              rgba(255,255,255,0.03) 4px
            )
          `,
          opacity: 0.5
        }} />

        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          padding: '25px 20px',
          textAlign: 'center',
          position: 'relative',
          zIndex: 1
        }}>
          <div style={{
            display: 'flex',
            justifyContent: 'space-between',
            alignItems: 'center',
            flexWrap: 'wrap',
            gap: '20px'
          }}>
            {/* 左側：ブランド情報 */}
            <div style={{ textAlign: 'left', display: 'flex', alignItems: 'center', gap: '10px' }}>
              <img 
                src="/icon.png" 
                alt="DocuMind Pro" 
                style={{
                  width: '32px',
                  height: '32px',
                  borderRadius: '6px',
                  opacity: 0.9
                }}
              />
              <div>
                <h4 style={{
                  margin: '0 0 4px 0',
                  fontSize: '16px',
                  fontWeight: '600',
                  color: '#ffffff'
                }}>
                  DocuMind Pro
                </h4>
                <p style={{
                  margin: '0',
                  fontSize: '13px',
                  color: '#b8c6db'
                }}>
                  Enterprise Document Intelligence Platform
                </p>
              </div>
            </div>

            {/* 中央：技術情報 */}
            <div style={{ textAlign: 'center' }}>
              <p style={{
                margin: '0 0 5px 0',
                fontSize: '13px',
                color: '#b8c6db'
              }}>
                <FaRobot style={{ marginRight: '4px', fontSize: '14px' }} />
                ハイブリッド検索 (セマンティック + キーワード)
              </p>
              <p style={{
                margin: '0',
                fontSize: '12px',
                color: '#8892b0'
              }}>
                Powered by OpenAI API & ChromaDB
              </p>
            </div>

            {/* 右側：バージョン・ステータス */}
            <div style={{ textAlign: 'right' }}>
              <div style={{
                background: 'rgba(44, 90, 160, 0.8)',
                border: '1px solid rgba(255,255,255,0.2)',
                padding: '4px 10px',
                borderRadius: '4px',
                fontSize: '11px',
                fontWeight: '500',
                color: '#ffffff',
                display: 'inline-block'
              }}>
                v1.0.0 BETA
              </div>
            </div>
          </div>

          {/* 区切り線 */}
          <div style={{
            width: '100%',
            height: '1px',
            background: 'rgba(255,255,255,0.1)',
            margin: '20px 0 15px 0'
          }} />

          {/* コピーライト */}
          <p style={{
            margin: '0',
            fontSize: '12px',
            color: '#8892b0',
            textAlign: 'center'
          }}>
            © 2024 DocuMind Pro. All rights reserved. | Built with ❤️ for enterprise productivity
          </p>
        </div>
      </footer>
    </div>
  );
};

export default App;
