import React, { useState, useEffect } from 'react';
import FileUpload from '../components/FileUpload';
import { UploadResponse } from '../types';
import { getDocumentCount, clearDocuments } from '../services/api';

interface UploadPageProps {
  onDocumentCountChange: (count: number) => void;
}

const UploadPage: React.FC<UploadPageProps> = ({ onDocumentCountChange }) => {
  const [documentCount, setDocumentCount] = useState(0);
  const [uploadMessage, setUploadMessage] = useState('');
  const [error, setError] = useState('');
  const [uploadHistory, setUploadHistory] = useState<Array<{
    filename: string;
    timestamp: string;
    chunks: number;
  }>>([]);

  useEffect(() => {
    fetchDocumentCount();
  }, []);

  const fetchDocumentCount = async () => {
    try {
      const response = await getDocumentCount();
      setDocumentCount(response.count);
      onDocumentCountChange(response.count);
    } catch (error) {
      console.error('„Éâ„Ç≠„É•„É°„É≥„ÉàÊï∞„ÅÆÂèñÂæó„Å´Â§±Êïó:', error);
    }
  };

  const handleUploadSuccess = (response: UploadResponse) => {
    setUploadMessage(`‚úÖ ${response.message}`);
    setDocumentCount(response.total_documents);
    onDocumentCountChange(response.total_documents);
    setError('');

    // „Ç¢„ÉÉ„Éó„É≠„Éº„ÉâÂ±•Ê≠¥„Å´ËøΩÂä†
    const filename = response.message.match(/File (.+) uploaded/)?.[1] || 'Unknown file';
    setUploadHistory(prev => [{
      filename,
      timestamp: new Date().toLocaleString('ja-JP'),
      chunks: response.chunks_created
    }, ...prev.slice(0, 4)]); // ÊúÄÊñ∞5‰ª∂„Åæ„Åß‰øùÊåÅ

    setTimeout(() => setUploadMessage(''), 5000);
  };

  const handleUploadError = (errorMessage: string) => {
    setError(`‚ùå ${errorMessage}`);
    setUploadMessage('');
  };

  const handleClearDocuments = async () => {
    if (window.confirm('„Åô„Åπ„Å¶„ÅÆ„Éâ„Ç≠„É•„É°„É≥„Éà„ÇíÂâäÈô§„Åó„Åæ„Åô„ÅãÔºü')) {
      try {
        await clearDocuments();
        setDocumentCount(0);
        onDocumentCountChange(0);
        setUploadHistory([]);
        setUploadMessage('‚úÖ „Åô„Åπ„Å¶„ÅÆ„Éâ„Ç≠„É•„É°„É≥„Éà„ÅåÂâäÈô§„Åï„Çå„Åæ„Åó„Åü');
        setTimeout(() => setUploadMessage(''), 3000);
      } catch (error: any) {
        setError(`ÂâäÈô§„Ç®„É©„Éº: ${error.response?.data?.detail || error.message}`);
      }
    }
  };

  return (
    <div style={{ padding: '30px', minHeight: '100%', display: 'flex', flexDirection: 'column' }}>
      {/* „Éö„Éº„Ç∏„Çø„Ç§„Éà„É´ */}
      <div style={{ textAlign: 'center', marginBottom: '30px' }}>
        <h1 style={{ 
          fontSize: '28px', 
          margin: '0 0 12px 0', 
          color: '#1a1a2e',
          fontWeight: '600'
        }}>
          üì§ PDF„Éï„Ç°„Ç§„É´ÁÆ°ÁêÜ
        </h1>
        <p style={{ 
          fontSize: '15px', 
          color: '#666', 
          margin: '0',
          lineHeight: '1.5'
        }}>
          PDF„Éï„Ç°„Ç§„É´„Çí„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ„Åó„Å¶„ÄÅAIÊ§úÁ¥¢„ÉªË≥áÊñôÁîüÊàê„ÅÆ„Åü„ÇÅ„ÅÆ„Éá„Éº„Çø„Éô„Éº„Çπ„ÇíÊßãÁØâ„Åó„Åæ„Åô
        </p>
      </div>

      {/* ÁèæÂú®„ÅÆ„Éâ„Ç≠„É•„É°„É≥„ÉàÁä∂Ê≥Å */}
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '20px',
        padding: '15px',
        backgroundColor: '#f8f9fa',
        borderRadius: '8px',
        border: '1px solid #e9ecef'
      }}>
        <div>
          <span style={{ fontWeight: 'bold' }}>
            ÁôªÈå≤Ê∏à„Åø„Éâ„Ç≠„É•„É°„É≥„Éà: {documentCount}‰ª∂
          </span>
        </div>
        {documentCount > 0 && (
          <button
            onClick={handleClearDocuments}
            style={{
              backgroundColor: '#dc3545',
              color: 'white',
              border: 'none',
              padding: '8px 16px',
              borderRadius: '4px',
              cursor: 'pointer',
              fontSize: '14px'
            }}
          >
            „Åô„Åπ„Å¶ÂâäÈô§
          </button>
        )}
      </div>

      {/* „É°„ÉÉ„Çª„Éº„Ç∏Ë°®Á§∫ */}
      {(uploadMessage || error) && (
        <div style={{
          padding: '15px',
          marginBottom: '30px',
          borderRadius: '8px',
          backgroundColor: error ? '#f8d7da' : '#d4edda',
          border: error ? '1px solid #f5c6cb' : '1px solid #c3e6cb',
          color: error ? '#721c24' : '#155724',
          fontSize: '16px',
          textAlign: 'center',
          fontWeight: 'bold'
        }}>
          {uploadMessage || error}
        </div>
      )}

      <div style={{ marginBottom: '40px' }}>
        <h2 style={{ marginBottom: '20px', color: '#333' }}>
          üì§ PDF„Éï„Ç°„Ç§„É´„Ç¢„ÉÉ„Éó„É≠„Éº„Éâ
        </h2>
        <FileUpload
          onUploadSuccess={handleUploadSuccess}
          onUploadError={handleUploadError}
        />
      </div>

      {/* „Ç¢„ÉÉ„Éó„É≠„Éº„ÉâÂ±•Ê≠¥ */}
      {uploadHistory.length > 0 && (
        <div style={{
          backgroundColor: 'white',
          border: '2px solid #e9ecef',
          borderRadius: '12px',
          padding: '30px',
          boxShadow: '0 4px 6px rgba(0,0,0,0.1)'
        }}>
          <h3 style={{ 
            marginBottom: '20px', 
            color: '#333',
            fontSize: '20px',
            textAlign: 'center'
          }}>
            üìã ÊúÄËøë„ÅÆ„Ç¢„ÉÉ„Éó„É≠„Éº„ÉâÂ±•Ê≠¥
          </h3>
          <div style={{ display: 'grid', gap: '12px' }}>
            {uploadHistory.map((item, index) => (
              <div
                key={index}
                style={{
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center',
                  padding: '15px',
                  backgroundColor: '#f8f9fa',
                  borderRadius: '8px',
                  border: '1px solid #e9ecef'
                }}
              >
                <div>
                  <div style={{ fontWeight: 'bold', fontSize: '16px', marginBottom: '4px' }}>
                    üìÑ {item.filename}
                  </div>
                  <div style={{ fontSize: '14px', color: '#666' }}>
                    {item.timestamp}
                  </div>
                </div>
                <div style={{
                  backgroundColor: '#007bff',
                  color: 'white',
                  padding: '6px 12px',
                  borderRadius: '12px',
                  fontSize: '12px',
                  fontWeight: 'bold'
                }}>
                  {item.chunks}„ÉÅ„É£„É≥„ÇØ
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* ‰ΩøÁî®ÊñπÊ≥ï„Ç¨„Ç§„Éâ */}
      <div style={{
        backgroundColor: '#f8f9fa',
        border: '1px solid #e9ecef',
        borderRadius: '8px',
        padding: '25px',
        marginTop: '30px'
